INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Monster (HB)'), 'Feature: A Little Off', 'You have a reputation for being a little odd, almost as if you''re uncomfortable in your own skin and around people. Whenever you act in a way that is strange or taboo, but not intentionally offensive, dangerous, or against the law, people tend to ignore you. This lets you get away with strange behavior like showing up at parties you weren''t invited to, pushing through a crowd to shake a noble''s hand, eating food off of someone else''s plate, or taking a nap in a public place. Most people brush you off as weird, but harmless.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Monster (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I only use my hands and not utensils when I eat.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I growl when I am threatened and purr when I am content.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'When someone asks me about my past, I change the subject.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I prefer all my meat rare (or raw).');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Darkness and shadows are a comfort to me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I never back down from a fight.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I embrace every comfort civilization has to offer with gusto.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I prefer the company of animals to people.');
