INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Monster (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'An adventurer I wronged as a monster hunts me.');
