INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Feytouched (HB)', 'You were kidnapped as a young child by the Fey and spirited away to the Feywild. Within the confines of this mysterious magical realm, those taken in such a manner usually do not survive long especially when Hags or other dark Fey creatures are involved. However, some do manage to avoid such a fate and are raised as slaves, servants, playthings, or companions, etc.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)'), 'Feature: Kindred Spirit', 'Even though you may not be of actual Fey blood or racial stock, all Fey you encounter will recognize you as Feytouched and treat you as one of their own. You know the customs and culture of Fey society and find acceptance in their communities.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m oblivious to etiquette and social expectations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I eat like a pig and have bad manners.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I hide scraps of food and trinkets away in my pockets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I feel tremendous empathy for all who suffer.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I live for the thrill of discovery.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I tell anyone and everyone about the things that torment me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I refuse to sit still, I will never reveal why.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am always the outsider looking in.');
