INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'I strive to find truth, no matter how crazy it seems.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '7', 'I seek to unlock the very secrets of reality.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '8', 'Beauty. We do our work to make the world a more beautiful place.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)'), 'Suggested Characteristics', 'Bond', '1d8');
