INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)'), 'Suggested Characteristics', 'Flaw', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I talk to spirits that no one else can see.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I say whatever comes to my mind.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I have certain rituals that I must follow every day. I can never break them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I have an addiction, or several.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I often forget what happened in the past, and am always mixing my history up.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'When nervous tends to repeat the same phrases over and over again.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '7', 'I have no concept of the value of money and place little importance on it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Feytouched (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '8', 'I reject conventional society norms and advocate simple, idealistic values others consider odd.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Farmer (HB)', 'You were what most common folk are - a farmer. You worked on a lord''s estate, their land, or you worked for a company and you produced resources from the land in return for food, shelter, clothing, protection, and other benefits. Farmers focus on raising living things and harvesting various useful goods - mostly food - from the land. Farmers make up the vast majority of the peasantry, alongside other raw resource producers, such as miners and fishermen. Farmers are a hearty, enduring folk, they work long hours under the sun often doing dirty and exhausting or even disgusting work.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Farm', 'Farm', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Farm' ORDER BY id DESC LIMIT 1), '1', 'Rancher. You raised animals for slaughter most likely for food, but also for leather, furs, and other useful materials.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Farm' ORDER BY id DESC LIMIT 1), '2', 'Grazier. You harvested goods produced by animals such as milk, eggs, feathers, or wool.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Farm' ORDER BY id DESC LIMIT 1), '3', 'Stockman. You bred animals to sell for work or as pets such as guard dogs, racing horses, and griffons.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Farm' ORDER BY id DESC LIMIT 1), '4', 'Peasant. You harvested edible or useful plants such as wheat, corn crops, or apples at an orchard.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Farm' ORDER BY id DESC LIMIT 1), '5', 'Forester. You grew trees for ornament or construction such as trees grown for bow staves, boat masts, or buildings.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Farm' ORDER BY id DESC LIMIT 1), '6', 'Silk. You raised silk worms or another exotic animal, and harvested their silk.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Feature: Of The land', 'You lack the formal education of the higher classes, but for how little you may know there is a great deal that you understand. You have a practical, working knowledge of astrology and meteorology, and can easily track the time and date as well as predict the weather within a few hours, or days if you''re lucky. You understand the "secrets of life"; the needs of life (water, light, air, salt, etc.), the patterns of physical inheritance, and the connections between different plants and animals.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Variant Feature: Of The People', 'For better or for worse, you are just very normal. Among commoners and on the road, most people just don''t notice you, and security checks at city gates or on the road are often brisk and half-hearted. People tend to trust that you''re just another face in the crowd. However, whenever you are some place commoners should not be, or normally are not seen, you stand out like a sore thumb and draw a good deal of attention, one way or another. You are unlikely to be preceded by your reputation, if you even have one, and if you do, nobody expects you to be them.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Variant Farmer: Fieldhand', 'From working away endlessly in the fields all day, you''ve become a pretty sturdy individual who can endure quite a lot. Those that face you know they''re in for a long and grueling fight.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Variant Feature: Work Until You Drop', 'Now when most think about a Farmer, they don''t exactly think of some complete badass going out to slay dragons, rescue consistently imperiled nobility, and save the day. More often than not they''re simply seen as peasant; background individuals that aren''t much of a threat. However, they can be a lot tougher than expected.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I''m always early to bed and early to rise.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I keep my gear and supplies in careful check.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I speak with a distinctive drawl or accent common to my class.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I always save up as much as I can, spending and consuming only what I must.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'When work needs doing, I do the work.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I keep constant track of the sun, moon, stars, seasons, and weather.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I like to sleep in the sun when I can.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I''m always chewing on a sprig of grass, a toothpick, tobacco, seeds, or preserved meat.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Fill the Need. There are a lot of hungry mouths in the world, with enough hands the world can feed them all. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'It''s a Living. I do what I''m good at because what I''m good at is good for me. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Brutal Practicality. Bury a fish or bury a man, just get the job done. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Hard Work. A labor of love is a purpose and meaning. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'A Simple Way of Life. If it ain''t broke, don''t fix it! (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Amusement. Playing a hand in the growth and development of things delights me. (Chaotic)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I want to find that special someone and raise a home full of children.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I''ll never forget the family farm I grew up on.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'Some day, I''ll be the lord of my own land.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I want to grow or raise the biggest or best example of my profession to prove I am the best!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My son was sent to war and never returned. I must find him!');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I am out to start a homestead of my own in an unexplored fringe territory.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Farmer (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I can be lazy and sleepy, sneaking off to take naps when work can wait.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I am absurdly isolated, innocent, or pious.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I believe in and obey my nation''s nobility blindly.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I abandoned my home to avoid taxation, conscription, or court and cannot return.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am not used to being wealthy and tend to overindulge.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Farmer (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I show off to attractive people too much.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Chef (HB)', 'You are someone who knows the value of a properly seasoned stew and can tell the difference between spices by sight, taste, scent, and even their texture. From the soldiers marching in the fields, to the warm haven of an inn, to the bowls of the largest keeps, to the homes of the common folk you know that food and need of food is what brings together everyone of every nation, religion, race, and creed. Food is your passion, it is your mission, it is your religion.', 'Choose two of History, Medicine, Nature, Survival', 'You are proficient with Cook''s Utensils', '', 'Cook''s Utensils, a cooking pot, a small knife, 2 full waterskins, 10 rations, and a book of your recipes', (SELECT id FROM core_book_sources WHERE raw_source = 'DnDBeyond Unearthed Arcana (Homebrew)'));
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Chef (HB)'), 'Feature: Passion for Food', 'You can find the choicest and best ingredients in just about any environment. For any roll relating to finding food or ingredients add 2d6 to your roll and for any food you make add 1d4 Hit Points to the creature who consumed the dish.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Chef (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Cooking is like a dance and the kitchen is my dance floor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My knowledge of flavor combinations can make even the most joyless eater smile.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'If I make a mistake or even slightly burn it I through it out. Thankfully I am great enough that I almost never have to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Once you step into the kitchen you step into my kingdom, and regardless if you are king or serf I will have obedience within it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Food is meant to make you smile. Food is my passion and my life, so I am always smiling.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'If you cause me to make a mistake in my cooking I am liable to try to put you in the pot next.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Do not call me a cook. I am a chef, a gourmet! To say otherwise is to invoke my wrath.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Recipes are meant to be passed down, so any chance I get to teach another to cook I do it with zeal.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Chef (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Adventurous. The only way to know if something tastes good or bad is to taste it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fame. I will sabotage and undercut other chefs to boost my own status and spread my fame faster. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Empathy. I don''t care who you are, if you are hungry I will feed you in a heartbeat. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Showmanship. I will turn the whole cooking process into a show to create smiles before the meal and serve them good food to make them smile after the meal too. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Respect. The reputation I have created has been no small feat in the making. I believe I am due a certain amount of respect for it. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Perfection. If I see one small misstep in the cooking process at the hands of a lesser chef or cook I will berate for them. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Chef (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I must pass on my recipes before I die.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The person that taught me to cook is always foremost in my mind when preparing food or making new recipes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am the last bastion of hope in a bland world. I will bring flavor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My tools were inherited so every nick, dent, and scratch is a fond memory for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I wish to feed the masses. Starvation is an abomination and I will slay it with tasty treats.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will become the best. I have to.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Chef (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have a tendency to taste all my recipes multiple times, sometimes eating the whole thing before it''s even done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I add bacon to EVERY recipe and believe it makes everything better.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I do not trust food or drink not prepared by me, and must check it for poison or worse too much salt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Sometimes in an effort to make a perfect dish I throw out perfectly fine dishes and end up wasting food.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I cannot hold my wine very well and often do things I regret after drinking it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Chef (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I secretly look down on the filthy masses that greedily eat sub par food as if it were the finest of dishes.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Gourmand (HB)', 'You are someone who knows the value of a properly seasoned stew and can tell the difference between spices by sight, taste, scent, and even their texture. From the soldiers marching in the fields, to the warm haven of an inn, to the bowls of the largest keeps, to the homes of the common folk you know that food and need of food is what brings together everyone of every nation, religion, race, and creed. Food is your passion, it is your mission, it is your religion.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)'), 'Feature: Chef in Sheep''s Clothing', 'In situations involving the social, political, and economic elite you are able to gain entry by your reputation and bearing alone, and may even be hired to cook some of your famous dishes. Wealthy people will often go out of their way to try to convince you to cook for them, you have advantage on persuasion rolls against them.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'Cooking is like a dance and the kitchen is my dance floor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My knowledge of flavor combinations can make even the most joyless eater smile.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'If I make a mistake or even slightly burn it I through it out. Thankfully I am great enough that I almost never have to.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'Once you step into the kitchen you step into my kingdom, and regardless if you are king or serf I will have obedience within it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'Food is meant to make you smile. Food is my passion and my life, so I am always smiling.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'If you cause me to make a mistake in my cooking I am liable to try to put you in the pot next.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'Do not call me a cook. I am a chef, a gourmet! To say otherwise is to invoke my wrath.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'Recipes are meant to be passed down, so any chance I get to teach another to cook I do it with zeal.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Adventurous. The only way to know if something tastes good or bad is to taste it. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Fame. I will sabotage and undercut other chefs to boost my own status and spread my fame faster. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Empathy. I don''t care who you are, if you are hungry I will feed you in a heartbeat. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Showmanship. I will turn the whole cooking process into a show to create smiles before the meal and serve them good food to make them smile after the meal too. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Respect. The reputation I have created has been no small feat in the making. I believe I am due a certain amount of respect for it. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Perfection. If I see one small misstep in the cooking process at the hands of a lesser chef or cook I will berate for them. (Evil)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I must pass on my recipes before I die.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The person that taught me to cook is always foremost in my mind when preparing food or making new recipes.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I am the last bastion of hope in a bland world. I will bring flavor.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'My tools were inherited so every nick, dent, and scratch is a fond memory for me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I wish to feed the masses. Starvation is an abomination and I will slay it with tasty treats.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I will become the best. I have to.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have a tendency to taste all my recipes multiple times, sometimes eating the whole thing before it''s even done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I add bacon to EVERY recipe and believe it makes everything better.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I do not trust food or drink not prepared by me, and must check it for poison or worse too much salt.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Sometimes in an effort to make a perfect dish I throw out perfectly fine dishes and end up wasting food.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I cannot hold my wine very well and often do things I regret after drinking it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Gourmand (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I secretly look down on the filthy masses that greedily eat sub par food as if it were the finest of dishes.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Courtesan (HB)', 'Nobles, politicians, criminals, the common man; hell, maybe even an orc or two. At some point in your life, you have been with all manner of society''s finest and worst through your profession. Your profession, whether through freedom of choice, or a cruel twist of unfortunate events, is one that utilizes a very specific set of skills: the art of seduction. Though your circumstances may be unfortunate, you use them to your advantage. Whether it is using your body to persuade a client to change their mind on an issue, using your unique skill set to distract clients while you steal from right under their noses, or even playing the part of a cheap assassin.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'Feature: Seducer''s Wiles', 'Your experiences with every creature have taught you how to use your tempting looks and inviting attitude to bring anyone of either gender in your embrace. You can call upon a creature  that can see and hear you, and gain its attention without it suspecting something (other than what you offer).');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'History of Service', 'History', '1d4');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'History' ORDER BY id DESC LIMIT 1), '1', 'I was under the wing of royalty, always loyal to their whims.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'History' ORDER BY id DESC LIMIT 1), '2', 'I have broken so many hearts, either for gold or fun.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'History' ORDER BY id DESC LIMIT 1), '3', 'I used to offer my services in a brothel.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'History' ORDER BY id DESC LIMIT 1), '4', 'My services always opened even the best locked doors.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'Variant Feature: Jaded', 'During your work, you''ve come across some truly twisted stuff, both people''s actions and the people themselves. Some of which you experienced much more closely than you''d probably have preferred. As such, precious little truly shocks you anymore. You tend to remain relatively calm and rational through events and scenes that leave others sick to their stomach.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I am highly flamboyant; I like to exaggerate my movements and mannerisms.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'My speech is like a switch; I speak either very warmly or very coldly to anyone.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I am unafraid to get up-close and "personal" with some people or situations.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I keep silent about any shortcomings I may be experiencing. I can''t stand being pitied.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I am extremely flirtatious, even with friends.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I prefer to leave things finished, rather then let bygones be bygones.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'I love being the center of attention, but I don''t envy those who take that from me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'I am quite hesitant to name my profession when asked about it.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Everyone uses others, why should I act any different? (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'I sympathize with those who work hard but gain little in their endeavors. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'I secretly love the work I do, despite what any naysayers may call me. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'When I''m doing you a service, I am yours. If I''m not, I want nothing to do with you. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'My favorite clients are the ones who are kind and generous to others. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'My body and soul are my own, no matter what anyone else may say about me. (Neutral)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I am highly skeptical when someone offers me something without asking for anything in return.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I don''t concern myself with the turmoil spawned from other''s problems.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'There is someone I''m waiting for so we can live together.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I know a secret about one of my clients that could cost me my life if revealed.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'My clients are all I have.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I have a child, and I would do anything for them.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Despite my own ability to read people, I myself am very easily read by others.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'If someone has some kind of debt over me, I feel obligated to fulfill said debt no matter what.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I sometimes expect or wish for others would take care of my problems.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Depending on the pay, I will do anything for a client.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I use any means I have at my disposal to complete a goal.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Courtesan (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I love presents or gifts, and cannot refuse one, even if I know doing so is a bad idea.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))', 'You are, or once were, a member of a cult — a religious splinter sect or a secret society of occult study. You might still be active within the cult, receiving orders from its secret masters. Or perhaps you''ve rejected its authority, and now the cult wants to bring you back into the fold... or silence you forever. In either case, you live at the crossroads of criminality and faith or arcane study.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))'), 'Feature: Cult Network', 'You know the locations of your cult''s safehouses, shrines, and sanctums in many settlements and wilderness areas. In such a network, you can find at least one loyal member of the cult, who can provide food and a safe place to rest for you and up to six people that you vouch for. Large cities always have a safehouse, while smaller settlements are 50% likely to have one. Remote wilderness areas have one safehouse in any 100-square-mile area. If you have left the cult, you can use the safehouse, drawing attention from the cult only 50% of the time.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))'), 'Suggested Characteristics', 'Personality Trait', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I try to hide as much of my face as possible, out of habit and fear of recognition.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'I believe that I possess esoteric wisdom on a wide variety of topics.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'Every event is part of the plan of the Secret Masters.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I carry the holy symbols and texts of a dozen religions, just in case.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'My outward cynicism hides a core of hope.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I have foreseen my own death, and have no fear of any other demise.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '7', 'The cult''s rites and taboos take up much of my spare time and energy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '8', 'When I meet new people, I look for the potential for new novices.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))'), 'Suggested Characteristics', 'Ideal', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Freedom. I will get the cult''s hooks out of my life, whatever it takes. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Tradition. My family has an ancient history with the cult, and I will honor it until my dying day. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Revelation. I must undermine the false dogmas that blind everyone around me. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Power. The cult is my path to something greater, and I will pay any cost. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Hierarchy. Some were made to serve, others to lead. (Neutral)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '6', 'Aspiration. The cult''s demands are harsh, but I will rise through the ranks. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'A priceless talisman of the cult was stolen, and I will earn great favor by returning it.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'The cult''s mysteriarch is like a mother to me; I value our relationship more than the cult itself.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'The gods I was raised to worship ignored my prayers as my loved ones lay dying, but the cult heard and answered.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'I must prevent the people I love from learning about my involvement with the cult.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I want to put my past with the cult behind me and atone for the harm I''ve done.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'A powerful inquisitor knows my face, and my connection to the cult.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I am quick to anger when my beliefs are slighted.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I weasel out of telling the plain truth when a question makes me uncomfortable.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I believe that everyone around me is hiding multiple layers of secrets.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Anyone who looks into my eyes can''t doubt that I am stark raving mad..');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have crossed over into death and returned, and it put a deep fear into me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew))') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I keep secrets even from those closest to me.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)', 'You are a member of a cult, either presently or formerly. You devoted long, strenuous days of your life towards whatever intangible, metaphysical force that guides your faith, but at last, that has changed. Perhaps your deity of choice reached out to you to give you a mission, perhaps you finally hung up the dark robes and ceremonial knife to start a new chapter in your life, whatever the case may be, you''ve decided to take what you''ve learned from your life as a worshiper of the ungodly, and embark on a new chapter of your life as an adventurer.
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)'), 'Feature: Shadow-Caller''s Instinct', 'As a cultist, you have a connection to places of worship, and the rituals used to summon extraplanar beings. You have advantage when making an Arcana or Religion check to uncover the origins or purpose of an object relating to Demons, Devils, or Aberrations. Talk to your DM if you wish to swap out one of these creature types for one that more closely resembles your cult''s theme.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)'), 'Variant Feature: Void-Touched Charisma', 'As a cultist, you have an innate connection to other followers of your faith, and can command the respect of followers of cults with similar values. You and your adventuring companions can expect to receive free healing and care at a outpost, shrine, or other established presence of your faith.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)'), 'Suggested Characteristics', 'Personality Trait', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '1', 'I see omens in every event and action. The gods try to speak to us we just need to listen.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '2', 'Nothing can shake my optimistic attitude.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '3', 'I quote (or misquote) sacred texts and proverbs in almost every situation.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '4', 'I am tolerant (or intolerant) of other faiths and respect (or condemn) the worship of other gods.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '5', 'I''ve enjoyed fine food, drink, and high society among my temple''s elite. Rough living grates on me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Personality Trait' ORDER BY id DESC LIMIT 1), '6', 'I''ve spent so long in the temple that I have little practical experience dealing with people in the outside world.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)'), 'Suggested Characteristics', 'Ideal', '1d5');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '1', 'Tradition. The ancient traditions of worship and sacrifice must be preserved and upheld. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '2', 'Change. We must help bring about the changes the gods are constantly working in the world. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '3', 'Power. I hope to one day rise to the top of my faith''s religious hierarchy. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '4', 'Faith. I trust that my deity will guide my actions. I have faith that if I work hard, things will go well. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Ideal' ORDER BY id DESC LIMIT 1), '5', 'Aspiration. I seek to prove myself worthy of my god''s favor by matching my actions against his or her teachings. (Any)');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)'), 'Suggested Characteristics', 'Bond', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '1', 'I would die to recover an ancient relic of my faith that was lost long ago.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '2', 'I will someday get revenge on the corrupt temple hierarchy who branded me a heretic.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '3', 'I owe my life to the priest who took me in when my parents died.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '4', 'Everything I do is for the common people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '5', 'I will do anything to protect the temple where I served.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Bond' ORDER BY id DESC LIMIT 1), '6', 'I seek to preserve a sacred text that my enemies consider heretical and seek to destroy.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I judge others harshly, and myself even more severely.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I put too much trust in those who wield power within my temple''s hierarchy.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'My piety sometimes leads me to blindly trust those that profess faith in my god.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I am inflexible in my thinking.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am suspicious of strangers and expect the worst of them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Cultist (HB) (DnDBeyond Unearthed Arcana (Homebrew),)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Once I pick a goal, I become obsessed with it to the detriment of everything else in my life.');
INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Runaway Noble (HB)', 'You are familiar with wealth and status. You once carried a noble title, and your family still owns land, collects taxes, and wields significant political influence. Something in your past caused you to run from your family. Be it a banishment, voluntary exile to protect your family, or another reason, you no longer reap the rewards of your family''s name. If your family still holds status and your relationship with them is still favorable, perhaps they send you gifts in hopes that you return safely. Alternatively, perhaps you were forced into exile and your family has stripped everything from you, including your name and rank.
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)'), 'Reason for Running Away', 'Reason for Running Away', '1d8');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '1', 'My family was disgraced and lost everything. I am on a quest to redeem their name and help them rise to prominence once more.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '2', 'Life was miserable for me growing up. As soon as I was able to, I gathered what I could and left to seek a life of adventure.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '3', 'I had to leave to protect my house. An outside force threatened or blackmailed me to leave or else my house would suffer the consequences.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '4', 'I fell in with a bad crowd and absconded with a portion of my family''s wealth. Returning home could mean imprisonment or death.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '5', 'I found out a dark secret. Now my conscience won''t let me stay. I must decide whether to protect or expose my house.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '6', 'My family has harsh coming-of-age expectations. I was expected to face a near-impossible task and chose to run away instead. My shame haunts me and my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '7', 'I fell in love with a member of a rival family and was ostracized for it. I hope to one day restore peace between our families with a marriage of our houses.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Reason for Running Away' ORDER BY id DESC LIMIT 1), '8', 'My sovereign ordered me to dismantle or destroy my house. I became a fugitive to protect them from myself and my vows.');
INSERT INTO background_features (background_id, name, description) VALUES ((SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)'), 'Feature: Knowledge of Status', 'Despite your circumstances, you are of noble birth. While you have been shunned from noble society for now, you still have a detailed knowledge of its inner workings. You know all the best masquerades, balls, and dances, as well as how to act around other nobility. Some common folk may still look up to you, and might even go out of their way to help you. You know all of the local lords and ladies, and for better or worse, they know of you. If you so choose, you may secure an audience with a local noble. Though, be aware as this may have consequences and could end with you in chains.');
INSERT INTO background_tables (background_id, parent_trait_name, title, dice_formula) VALUES ((SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)'), 'Suggested Characteristics', 'Flaw', '1d6');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I often talk wistfully about my house and how I long to return some day.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'As soon as I left, I hardened myself to the realities of the world, becoming cold and bitter.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I still carry myself with dignity and poise, reminding everyone around me that I am still of noble birth.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I constantly write home to a few family members that still desire to keep in contact with me.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I have come to expect a certain level of decorum around me and will chastise those who are impolite or rude.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'I do not hold myself any higher than others, regardless of where I come from. We are all equal.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '7', 'If you hurt me or mine, I will not stop hunting you until you are dead or worse.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '8', 'I have lost my friends and family, and this sadness follows me wherever I go.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'Respect. Respect is still due to me because of my birthright, but all people regardless of station deserve to be treated with dignity. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'Responsibility. One day I will return home to face my family, whether I return victorious or in chains. (Lawful)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'Independence. I will do everything in my power to remain free of the bondage of my house. (Chaotic)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'Power. I will crush my family into dust and from their ashes I will raise a new, stronger house. (Evil)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Family. Nothing is more important than helping my family in any way I can. (Any)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'Noble Reclamation. For the good of my family, the realm, or even the world, I must restore my family''s good name. (Good)');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I will face any test to win back the approval of my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'My house''s alliance with another noble family must be rebuilt at all costs.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I love my family more than anything, and I have sacrificed my lifestyle to protect them.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I had a romantic tryst with the heir of a family that my family despises. I love them still, and am constantly torn between them and my family.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'Despite my circumstances, my loyalty to my sovereign is unwavering, and I will serve with or without my house.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '6', 'With my fall from my house, I have become an icon of the common people.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '1', 'I have lost everything and my broken pride makes me hesitant to accept help.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '2', 'I hide a truly scandalous secret that could ruin my family forever.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '3', 'I will leap to defend my family''s name, with often violent results.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '4', 'I cannot control certain urges that have earned me my family''s ire.');
INSERT INTO background_table_rows (table_id, row_number, row_text) VALUES ((SELECT id FROM background_tables WHERE background_id = (SELECT id FROM backgrounds WHERE name = 'Runaway Noble (HB)') AND title = 'Flaw' ORDER BY id DESC LIMIT 1), '5', 'I am oblivious to anything that does not involve me.');
