INSERT INTO backgrounds (name, flavor_text, skills_text, tools_text, languages_text, equipment_text, book_source_id) VALUES ('Traveling Cook (HB)', 'You''ve travelled the world in pursuit of your culinary passions, and have learned much from your journeys. You go wherever great food takes you, spreading your knowledge and hoping to pick up some things along the way.', 'Performance, Survival', 'Cook''s Utensils', 'You know most food and cooking terms from all standard languages', 'A set of cook''s utensils, traveler''s clothes, an iron pot, a jar of spices, a small leather book of collected recipes, and a pouch containing 10 gp', (SELECT id FROM core_book_sources WHERE raw_source = 'Petes Mgeets (Homebrew)'));
