PRAGMA foreign_keys = ON;
BEGIN TRANSACTION;

-- === SEED: LANGUAGES (Elite Mapping) ===
INSERT OR IGNORE INTO core_languages (name, type, description, script, typical_speakers_json, entries_json, book_id, page)
SELECT v.n, v.t, v.d, v.sc, v.sp, v.en, b.id, v.p
FROM (
    SELECT 'Aarakocra' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature aarakocra}"]' AS sp, '[]' AS en, 'MM' AS b, 12 AS p
    UNION ALL SELECT 'Abanasinian' AS n, 'standard' AS t, NULL AS d, 'Common' AS sc, '["Abanasinia"]' AS sp, '[]' AS en, 'DSotDQ' AS b, 9 AS p
    UNION ALL SELECT 'Abyssal' AS n, 'exotic' AS t, NULL AS d, 'Infernal' AS sc, '["{@filter demons|bestiary|tag=demon}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Alzhedo' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Thorass' AS sc, '["Calishite ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Aven' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@race Aven|PSD}"]' AS sp, '[]' AS en, 'PSD' AS b, 6 AS p
    UNION ALL SELECT 'Blink Dog' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Blink Dog||blink dogs}"]' AS sp, '[]' AS en, 'MM' AS b, 318 AS p
    UNION ALL SELECT 'Bothii' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Uthgardt Barbarian Leader|SKT|Uthgardt barbarians}", "{@creature Uthgardt Shaman|SKT|Uthgardt shamans}"]' AS sp, '[]' AS en, 'SKT' AS b, 86 AS p
    UNION ALL SELECT 'Bullywug' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Bullywug||bullywugs}"]' AS sp, '[]' AS en, 'MM' AS b, 35 AS p
    UNION ALL SELECT 'Celestial' AS n, 'exotic' AS t, NULL AS d, 'Celestial' AS sc, '["{@filter celestials|bestiary|type=celestial}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Chessentan' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Thorass' AS sc, '["Mulan ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Chondathan' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Thorass' AS sc, '["Chondathan ethnic group", "Tethyrian ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Coalition Pidgin' AS n, 'standard' AS t, 'The humans of the Brazen Coalition speak a different language—a pidgin of Orc, Siren, and a native human language that is similar to Vampire (about as closely related as Italian is to Spanish). Orcs, sirens, and goblins also speak this Brazen Coalition pidgin.' AS d, NULL AS sc, '["Brazen Coalition", "{@race Human (Ixalan)|PSX|Humans}", "{@race Orc (Ixalan)|PSX|Orcs}", "{@race Siren|PSX|Sirens}", "{@race Goblin (Ixalan)|PSX|Goblins}"]' AS sp, '["The humans of the Brazen Coalition speak a different language—a pidgin of Orc, Siren, and a native human language that is similar to {@language Vampire|PSX} (about as closely related as Italian is to Spanish). Orcs, sirens, and goblins also speak this Brazen Coalition pidgin."]' AS en, 'PSX' AS b, 6 AS p
    UNION ALL SELECT 'Common' AS n, 'standard' AS t, NULL AS d, 'Common' AS sc, '["{@filter humans|bestiary|type=humanoid|tag=any race;human}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Common Trade Pidgin' AS n, 'standard' AS t, 'A trade pidgin used in circumstances where members of different peoples do interact with each other. But this language is almost impossible to use for discussions of matters of any depth.' AS d, NULL AS sc, '["Sun Empire", "River Heralds", "Legion of Dusk", "Brazen Coalition", "{@race Human (Ixalan)|PSX|Humans}", "{@race Orc (Ixalan)|PSX|Orcs}", "{@race Siren|PSX|Sirens}", "{@race Goblin (Ixalan)|PSX|Goblins}"]' AS sp, '["A trade pidgin used in circumstances where members of different peoples do interact with each other. But this language is almost impossible to use for discussions of matters of any depth."]' AS en, 'PSX' AS b, 6 AS p
    UNION ALL SELECT 'Daelkyr' AS n, 'exotic' AS t, NULL AS d, 'Daelkyr' AS sc, '["{@filter aberrations|bestiary|type=aberration|source=ERLW}", "denizens of Khyber"]' AS sp, '[]' AS en, 'ERLW' AS b, 6 AS p
    UNION ALL SELECT 'Damaran' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Dethek' AS sc, '["Damaran ethnic group", "Nar ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Dambrathan' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Espruar' AS sc, '["Arkaiun ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Deep Crow' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Deep Crow|AI|deep crows}"]' AS sp, '[]' AS en, 'AI' AS b, 210 AS p
    UNION ALL SELECT 'Deep Speech' AS n, 'exotic' AS t, NULL AS d, 'none' AS sc, '["{@creature mind flayer||mind flayers}", "{@creature beholder||beholders}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Demonic' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@filter Demons|bestiary|source=|tag=demon}", "{@filter Devils|bestiary|source=|tag=devil}"]' AS sp, '[]' AS en, 'PSI' AS b, 9 AS p
    UNION ALL SELECT 'Draconic' AS n, 'exotic' AS t, 'Draconic is thought to be one of the oldest languages and is often used in the study of magic. The language sounds harsh to most other creatures and includes numerous hard consonants and sibilants.' AS d, 'Draconic' AS sc, '["{@filter dragons|bestiary|type=dragon}", "{@race dragonborn}"]' AS sp, '["Draconic is thought to be one of the oldest languages and is often used in the study of magic. The language sounds harsh to most other creatures and includes numerous hard consonants and sibilants."]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Druidic' AS n, 'secret' AS t, 'Druidic is the secret language of druids. It can be spoken or used to leave hidden messages. Creatures who know this language automatically spot such a message. Others spot the message''s presence with a successful 15 Wisdom (Perception) check but can''t decipher it without magic.' AS d, NULL AS sc, '["{@class druid||druids}/{@creature druid||druids}", "{@creature treant||treants}"]' AS sp, '["Druidic is the secret language of druids. It can be spoken or used to leave hidden messages. Creatures who know this language automatically spot such a message. Others spot the message''s presence with a successful {@dc 15} Wisdom ({@skill Perception}) check but can''t decipher it without magic."]' AS en, 'PHB' AS b, 64 AS p
    UNION ALL SELECT 'Dwarvish' AS n, 'standard' AS t, 'Dwarvish is full of hard consonants and guttural sounds, and those characteristics spill over into whatever other language a dwarf might speak.' AS d, 'Dwarvish' AS sc, '["{@filter dwarves|bestiary|type=humanoid|tag=any race;dwarf}"]' AS sp, '["Dwarvish is full of hard consonants and guttural sounds, and those characteristics spill over into whatever other language a dwarf might speak."]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Elvish' AS n, 'standard' AS t, 'Elvish is fluid, with subtle intonations and intricate grammar. Elven literature is rich and varied, and their songs and poems are famous among other races. Many bards learn their language so they can add Elvish ballads to their repertoires.' AS d, 'Elvish' AS sc, '["{@filter elves|bestiary|type=humanoid|tag=any race;elf}"]' AS sp, '["Elvish is fluid, with subtle intonations and intricate grammar. Elven literature is rich and varied, and their songs and poems are famous among other races. Many bards learn their language so they can add Elvish ballads to their repertoires."]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Ergot' AS n, 'standard' AS t, NULL AS d, 'Common' AS sc, '["Northern Ergoth"]' AS sp, '[]' AS en, 'DSotDQ' AS b, 9 AS p
    UNION ALL SELECT 'Giant' AS n, 'standard' AS t, NULL AS d, 'Dwarvish' AS sc, '["{@creature ogre||ogres}", "{@filter giants|bestiary|type=giant}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Giant Eagle' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Giant Eagle||giant eagles}"]' AS sp, '[]' AS en, 'MM' AS b, 324 AS p
    UNION ALL SELECT 'Giant Elk' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Giant Elk||giant elks}"]' AS sp, '[]' AS en, 'MM' AS b, 325 AS p
    UNION ALL SELECT 'Giant Owl' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Giant Owl||giant owls}"]' AS sp, '[]' AS en, 'MM' AS b, 327 AS p
    UNION ALL SELECT 'Gith' AS n, 'standard' AS t, 'The gith use a written language composed of alphabetic symbols arranged in circular clusters called tir''su. Each "spoke" on the wheel corresponds to a letter of the alphabet. Each cluster of characters represents a single word, and multiple tir''su connect to form phrases and sentences.
Githyanki and githzerai both speak Gith, but each race has a distinct dialect and accent. Similarly, the two races of gith differentiate their language by how they write it. Githyanki write a tir''su clockwise, starting at the top. Githzerai use the same letter symbols but write their tir''su counterclockwise, starting from the bottom.' AS d, 'Tir''su' AS sc, '["{@creature Githzerai Monk||githzerai monks}", "{@creature Githyanki Warrior||githyanki warriors}", "{@creature Githzerai Zerth||githzerai zerths}", "{@creature Githyanki Knight||githyanki knights}"]' AS sp, '[{"type": "inset", "name": "The Gith Alphabet", "entries": ["The gith use a written language composed of alphabetic symbols arranged in circular clusters called tir''su. Each \"spoke\" on the wheel corresponds to a letter of the alphabet. Each cluster of characters represents a single word, and multiple tir''su connect to form phrases and sentences.", "Githyanki and githzerai both speak Gith, but each race has a distinct dialect and accent. Similarly, the two races of gith differentiate their language by how they write it. Githyanki write a tir''su clockwise, starting at the top. Githzerai use the same letter symbols but write their tir''su counterclockwise, starting from the bottom."]}]' AS en, 'MM' AS b, 160 AS p
    UNION ALL SELECT 'Gnoll' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Gnoll||gnolls}", "{@creature Gnoll Pack Lord||gnoll pack lords}", "{@creature Gnoll Fang of Yeenoghu||gnoll fangs of Yeenoghu}"]' AS sp, '[]' AS en, 'MM' AS b, 163 AS p
    UNION ALL SELECT 'Gnomish' AS n, 'standard' AS t, 'The Gnomish language, which uses the Dwarvish script, is renowned for its technical treatises and its catalogs of knowledge about the natural world.' AS d, 'Dwarvish' AS sc, '["{@filter gnomes|bestiary|type=humanoid|tag=any race;gnome}"]' AS sp, '["The Gnomish language, which uses the Dwarvish script, is renowned for its technical treatises and its catalogs of knowledge about the natural world."]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Goblin' AS n, 'standard' AS t, NULL AS d, 'Dwarvish' AS sc, '["{@filter goblinoids|bestiary|tag=goblinoid}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Grell' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Grell||grells}"]' AS sp, '[]' AS en, 'MM' AS b, 172 AS p
    UNION ALL SELECT 'Grung' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Grung|VGM|grungs}", "{@creature Grung Wildling|VGM|grung wildlings}", "{@creature Grung Elite Warrior|VGM|grung elite warriors}"]' AS sp, '[]' AS en, 'VGM' AS b, 156 AS p
    UNION ALL SELECT 'Guran' AS n, 'standard' AS t, 'A patois of Roushoum and Rashemi.
Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Thorass' AS sc, '["Gur ethnic group"]' AS sp, '["A patois of {@language Roushoum|SCAG} and {@language Rashemi|SCAG}.", {"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Halfling' AS n, 'standard' AS t, 'The Halfling language isn''t secret, but halflings are loath to share it with others. They write very little, so they don''t have a rich body of literature. Their oral tradition, however, is very strong. Almost all halflings speak Common to converse with the people in whose lands they dwell or through which they are traveling.' AS d, 'Common' AS sc, '["{@filter halflings|bestiary|type=humanoid|tag=any race;halfling}"]' AS sp, '["The Halfling language isn''t secret, but halflings are loath to share it with others. They write very little, so they don''t have a rich body of literature. Their oral tradition, however, is very strong. Almost all halflings speak Common to converse with the people in whose lands they dwell or through which they are traveling."]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Halruaan' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Draconic' AS sc, '["Halruaan ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Homarid' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Homarid|PSD|homarids}"]' AS sp, '[]' AS en, 'PSD' AS b, 15 AS p
    UNION ALL SELECT 'Hook Horror' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Hook Horror||hook horrors}"]' AS sp, '[]' AS en, 'MM' AS b, 189 AS p
    UNION ALL SELECT 'Ice Toad' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature Ice Toad|RoT|ice toads}"]' AS sp, '[]' AS en, 'RoT' AS b, 90 AS p
    UNION ALL SELECT 'Illuskan' AS n, 'standard' AS t, 'Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.
If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals.' AS d, 'Thorass' AS sc, '["Illuskan ethnic group"]' AS sp, '[{"type": "inset", "name": "Option: Human Languages", "entries": ["Many human ethnicities and nations in Faerûn have their own language, in addition to Common. Most human languages are written in Thorass, the alphabet of Old Common, derived from the Chondathan language that traders used as their common tongue. A few human languages use other alphabets, including Draconic, Dethek, and Espruar.", "If your Dungeon Master allows this option, then any human from an ethnic group that has its own language is assumed to know that language as well as Common. This ethnic language is treated as a free additional language for such individuals."]}]' AS en, 'SCAG' AS b, 112 AS p
    UNION ALL SELECT 'Infernal' AS n, 'exotic' AS t, NULL AS d, 'Infernal' AS sc, '["{@filter devils|bestiary|tag=devil}"]' AS sp, '[]' AS en, 'PHB' AS b, 123 AS p
    UNION ALL SELECT 'Istarian' AS n, 'rare' AS t, NULL AS d, 'Istarian' AS sc, '["Ancient Istarians"]' AS sp, '[]' AS en, 'DSotDQ' AS b, 9 AS p
    UNION ALL SELECT 'Itzocan' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["Sun Empire", "{@race Human (Ixalan)|PSX|Humans}"]' AS sp, '[]' AS en, 'PSX' AS b, 6 AS p
    UNION ALL SELECT 'Ixitxachitl' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@creature ixitxachitl|OotA}", "{@creature Ixitxachitl Cleric|OotA|ixitxachitl clerics}", "{@creature vampiric ixitxachitl|OotA}", "{@creature Vampiric Ixitxachitl Cleric|OotA|vampiric ixitxachitl clerics}"]' AS sp, '[]' AS en, 'OotA' AS b, 225 AS p
    UNION ALL SELECT 'Keldon' AS n, 'standard' AS t, NULL AS d, NULL AS sc, '["{@race Human (Keldon)|PSD|Keldons}"]' AS sp, '[]' AS en, 'PSD' AS b, 19 AS p
