-- === SEED: CONDITIONS (Elite Mapping) ===
INSERT OR IGNORE INTO core_conditions (name, description, is_disease, entries_json, page, book_id)
SELECT v.n, v.d, v.is_dis, v.en, v.p, b.id
FROM (
    SELECT 'Blinded' AS n, '- A blinded creature can''t see and automatically fails any ability check that requires sight.
- Attack rolls against the creature have advantage, and the creature''s attack rolls have disadvantage.' AS d, 0 AS is_dis, '[{"type": "list", "items": ["A blinded creature can''t see and automatically fails any ability check that requires sight.", "Attack rolls against the creature have advantage, and the creature''s attack rolls have disadvantage."]}]' AS en, 290 AS p, 'PHB' AS b
    UNION ALL SELECT 'Charmed' AS n, '- A charmed creature can''t attack the charmer or target the charmer with harmful abilities or magical effects.
- The charmer has advantage on any ability check to interact socially with the creature.' AS d, 0 AS is_dis, '[{"type": "list", "items": ["A charmed creature can''t attack the charmer or target the charmer with harmful abilities or magical effects.", "The charmer has advantage on any ability check to interact socially with the creature."]}]' AS en, 290 AS p, 'PHB' AS b
    UNION ALL SELECT 'Deafened' AS n, '- A deafened creature can''t hear and automatically fails any ability check that requires hearing.' AS d, 0 AS is_dis, '[{"type": "list", "items": ["A deafened creature can''t hear and automatically fails any ability check that requires hearing."]}]' AS en, 290 AS p, 'PHB' AS b
    UNION ALL SELECT 'Exhaustion' AS n, 'Some special abilities and environmental hazards, such as starvation and the long-term effects of freezing or scorching temperatures, can lead to a special condition called exhaustion. Exhaustion is measured in six levels. An effect can give a creature one or more levels of exhaustion, as specified in the effect''s description.
If an already exhausted creature suffers another effect that causes exhaustion, its current level of exhaustion increases by the amount specified in the effect''s description.
A creature suffers the effect of its current level of exhaustion as well as all lower levels. For example, a creature suffering level 2 exhaustion has its speed halved and has disadvantage on ability checks.
An effect that removes exhaustion reduces its level as specified in the effect''s description, with all exhaustion effects ending if a creature''s exhaustion level is reduced below 1.
Finishing a long rest reduces a creature''s exhaustion level by 1, provided that the creature has also ingested some food and drink. Also, being raised from the dead reduces a creature''s exhaustion level by 1.' AS d, 0 AS is_dis, '["Some special abilities and environmental hazards, such as starvation and the long-term effects of freezing or scorching temperatures, can lead to a special condition called exhaustion. Exhaustion is measured in six levels. An effect can give a creature one or more levels of exhaustion, as specified in the effect''s description.", {"type": "table", "colLabels": ["Level", "Effect"], "colStyles": ["col-1 text-center", "col-11"], "rows": [["1", "Disadvantage on ability checks"], ["2", "Speed halved"], ["3", "Disadvantage on attack rolls and saving throws"], ["4", "Hit point maximum halved"], ["5", "Speed reduced to 0"], ["6", "Death"]]}, "If an already exhausted creature suffers another effect that causes exhaustion, its current level of exhaustion increases by the amount specified in the effect''s description.", "A creature suffers the effect of its current level of exhaustion as well as all lower levels. For example, a creature suffering level 2 exhaustion has its speed halved and has disadvantage on ability checks.", "An effect that removes exhaustion reduces its level as specified in the effect''s description, with all exhaustion effects ending if a creature''s exhaustion level is reduced below 1.", "Finishing a long rest reduces a creature''s exhaustion level by 1, provided that the creature has also ingested some food and drink. Also, being raised from the dead reduces a creature''s exhaustion level by 1."]' AS en, 291 AS p, 'PHB' AS b
    UNION ALL SELECT 'Frightened' AS n, '- A frightened creature has disadvantage on ability checks and attack rolls while the source of its fear is within line of sight.
- The creature can''t willingly move closer to the source of its fear.' AS d, 0 AS is_dis, '[{"type": "list", "items": ["A frightened creature has disadvantage on ability checks and attack rolls while the source of its fear is within line of sight.", "The creature can''t willingly move closer to the source of its fear."]}]' AS en, 290 AS p, 'PHB' AS b
    UNION ALL SELECT 'Grappled' AS n, '- A grappled creature''s speed becomes 0, and it can''t benefit from any bonus to its speed.
- The condition ends if the grappler is incapacitated.
- The condition also ends if an effect removes the grappled creature from the reach of the grappler or grappling effect, such as when a creature is hurled away by the thunderwave spell.' AS d, 0 AS is_dis, '[{"type": "list", "items": ["A grappled creature''s speed becomes 0, and it can''t benefit from any bonus to its speed.", "The condition ends if the grappler is {@condition incapacitated}.", "The condition also ends if an effect removes the grappled creature from the reach of the grappler or grappling effect, such as when a creature is hurled away by the {@spell thunderwave} spell."]}]' AS en, 290 AS p, 'PHB' AS b
    UNION ALL SELECT 'Incapacitated' AS n, '- An incapacitated creature can''t take actions or reactions.' AS d, 0 AS is_dis, '[{"type": "list", "items": ["An incapacitated creature can''t take actions or reactions."]}]' AS en, 290 AS p, 'PHB' AS b
    UNION ALL SELECT 'Invisible' AS n, '- An invisible creature is impossible to see without the aid of magic or a special sense. For the purpose of hiding, the creature is heavily obscured. The creature''s location can be detected by any noise it makes or any tracks it leaves.
