-- schema_books.sql

DROP TABLE IF EXISTS core_type_books;
CREATE TABLE core_type_books (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL UNIQUE      -- Nome do tipo de livro (ex: 'Wizards of the Coast', 'Third-Party', 'Unearthed Arcana', 'Homebrew')
);


DROP TABLE IF EXISTS core_books;

CREATE TABLE core_books (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    code TEXT UNIQUE,        -- Código curto do livro (ex: 'PHB', 'DMG', 'XGE')
    name TEXT NOT NULL UNIQUE,        -- Nome completo do livro    
    release_date DATE,                -- Data de lançamento
    type_book_id INTEGER NOT NULL DEFAULT 1,
    FOREIGN KEY (type_book_id) REFERENCES core_type_books(id)
);


INSERT OR IGNORE INTO core_type_books (name) VALUES
