INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-aberrant-dragonmark','Aberrant Dragonmark','You have manifested an aberrant dragonmark. Determine its appearance and the flaw associated with it.',(SELECT id FROM core_books WHERE code='E:RLW'));
