INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-adept-of-the-black-robes','Adept of the Black Robes','You chose the moon Nuitari to influence your magic, and your ambition and loyalty to the Order of the Black Robes has been recognized, granting you these benefits',(SELECT id FROM core_books WHERE code='D:SDQ'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-adept-of-the-red-robes','Adept of the Red Robes','You chose the moon Lunitari to influence your magic, and your dedication to maintaining the balance between all things has been recognized by the Order of the Red Robes, granting you these benefits',(SELECT id FROM core_books WHERE code='D:SDQ'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-adept-of-the-white-robes','Adept of the White Robes','You chose the moon Solinari to influence your magic, and your oath to use magic to make the world a better place has been recognized by the Order of the White Robes, granting you these benefits',(SELECT id FROM core_books WHERE code='D:SDQ'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-agent-of-order','Agent of Order','You can channel cosmic forces of order to gain these benefits',(SELECT id FROM core_books WHERE code='PS:AiM'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-alert','Alert','Always on the lookout for danger',(SELECT id FROM core_books WHERE code='PHB'));
