INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-grudge-bearer','Grudge Bearer (UA)','You have a deep hatred for a particular kind of creature. Choose your foes, a type of creature to bear the burden of your wrath: aberrations, beasts, celestials, constructs, dragons, elementals, fey, fiends, giants, monstrosities, oozes, plants, or undead. Alternatively, you can choose two races of humanoid (such as gnolls and orcs).',(SELECT id FROM core_books WHERE code='UA'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-guile-of-the-cloud-giant','Guile of the Cloud Giant','You’ve manifested the confounding magic emblematic of cloud giants, granting you the following benefits',(SELECT id FROM core_books WHERE code='BPTG'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-gunner','Gunner','You have a quick hand and keen eye when employing firearms, granting you the following benefits',(SELECT id FROM core_books WHERE code='TCE'));
