INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-human-determination','Human Determination (UA)','You are filled with a determination that can draw the unreachable within your reach.',(SELECT id FROM core_books WHERE code='UA'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-infernal-constitution','Infernal Constitution','Fiendish blood runs strong in you, unlocking a resilience akin to that possessed by some fiends.',(SELECT id FROM core_books WHERE code='XGE'));
