INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-inspiring-leader','Inspiring Leader','You can spend 10 minutes inspiring your companions, shoring up their resolve to fight. When you do so, choose up to six friendly creatures (which can include yourself) within 30 feet of you who can see or hear you and who can understand you. Each creature can gain temporary hit points equal to your level + your Charisma modifier. A creature can''t gain temporary hit points from this feat again until it has finished a short or long rest.',(SELECT id FROM core_books WHERE code='PHB'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-keen-mind','Keen Mind','You have a mind that can track time, direction, and detail with uncanny precision.',(SELECT id FROM core_books WHERE code='PHB'));
