INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-knight-of-the-rose','Knight of the Rose','You are a Knight of Solamnia aligned with the Order of the Rose, a group known for leadership, justice, and wisdom. Your resolve grants you these benefits',(SELECT id FROM core_books WHERE code='D:SDQ'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-knight-of-the-sword','Knight of the Sword','You are a Knight of Solamnia aligned with the Order of the Sword, a group devoted to heroism and courage. Bravery steels your spirit, granting you these benefits',(SELECT id FROM core_books WHERE code='D:SDQ'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-lightly-armored','Lightly Armored','You have trained to master the use of light armor, gaining the following benefits.',(SELECT id FROM core_books WHERE code='PHB'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-linguist','Linguist','You have studied languages and codes, gaining the following benefits',(SELECT id FROM core_books WHERE code='PHB'));
