INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-mounted-combatant','Mounted Combatant','You are a dangerous foe to face while mounted. While you are mounted and aren''t incapacitated',(SELECT id FROM core_books WHERE code='PHB'));
