INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-righteous-heritor','Righteous Heritor','You can channel cosmic forces of good to gain these benefits',(SELECT id FROM core_books WHERE code='PS:AiM'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-ritual-caster','Ritual Caster','You have learned a number of spells that you can cast as rituals. These spells are written in a ritual book, which you must have in hand while casting one of them.',(SELECT id FROM core_books WHERE code='PHB'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-rune-shaper','Rune Shaper','You’ve studied the magic of Giant runes, granting you the following benefits',(SELECT id FROM core_books WHERE code='BPTG'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-savage-attacker','Savage Attacker','Once per turn when you roll damage for a melee weapon attack, you can reroll the weapon''s damage dice and use either total.',(SELECT id FROM core_books WHERE code='PHB'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-scion-of-the-outer-planes','Scion of the Outer Planes','Your connection to an Outer Plane infuses you with the energy there. Choose a type of plane listed in the Planar Infusion Table. Your choice gives you a resistance to a damage type and the ability to cast a cantrip, as specified in the table. You can cast this cantrip without material components, and your spellcasting ability for it is Intelligence, Wisdom or Charisma (choose when you select this feat).',(SELECT id FROM core_books WHERE code='PS:AiM'));
INSERT INTO feats (slug, name, description, core_book_id) VALUES ('feat-second-chance','Second Chance','Fortune favors you when someone tries to strike you.',(SELECT id FROM core_books WHERE code='XGE'));
