-- INSERTs for all feats (resolved to core_book code subqueries)
BEGIN TRANSACTION;

DROP TABLE IF EXISTS feats;
DROP TABLE IF EXISTS feat_prerequisites;
DROP TABLE IF EXISTS feat_features;

CREATE TABLE IF NOT EXISTS feats (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  slug TEXT NOT NULL UNIQUE,
  name TEXT,
  description TEXT,
  core_book_id INTEGER,
  FOREIGN KEY (core_book_id) REFERENCES core_books(id) ON DELETE SET NULL
);
CREATE TABLE IF NOT EXISTS feat_prerequisites (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  feat_id INTEGER NOT NULL,
  raw_text TEXT,
  FOREIGN KEY (feat_id) REFERENCES feats(id) ON DELETE CASCADE
);
CREATE TABLE IF NOT EXISTS feat_features (
  id INTEGER PRIMARY KEY AUTOINCREMENT,
  feat_id INTEGER NOT NULL,
  name TEXT,
  description TEXT,
  FOREIGN KEY (feat_id) REFERENCES feats(id) ON DELETE CASCADE
);

-- Índices para melhorar performance das consultas
CREATE INDEX IF NOT EXISTS idx_feats_core_book_id ON feats(core_book_id);
CREATE INDEX IF NOT EXISTS idx_feat_prerequisites_feat_id ON feat_prerequisites(feat_id);
CREATE INDEX IF NOT EXISTS idx_feat_features_feat_id ON feat_features(feat_id);


-- -- INSERTS: feats

-- -- INSERTS: feat_prerequisites

-- -- INSERTS: feat_features

COMMIT;